import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './estilos/PerUsuarioComun.css'; 
import DatosPersonales from './DatosUsuario';



const PerfilUsuario = () => {
  const [usuario, setUsuario] = useState({});
  const [editando, setEditando] = useState(false);
  const [oficio, setOficio] = useState('');
const [empresa, setEmpresa] = useState('');
const [descripcion, setDescripcion] = useState('');

useEffect(() => {
  const token = localStorage.getItem('token');
  if (token) {
    axios
      .get("http://localhost:3000/usuario/profesional/perfil", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        console.log('Datos del profesional:', response.data);
        setOficio(response.data.oficio?.nombre || '');
        setEmpresa(response.data.empresa?.nombre || '');
        setDescripcion(response.data.descripcion || '');
      })
      .catch((error) => {
        console.error('Error al cargar datos del profesional:', error);
      });
  } else {
    console.log('No hay token');
  }
}, []);

