import React, { useState, useEffect } from 'react';
import axios from 'axios';

//const ImagenesProf = () => {
  const ImagenesProf = ({ idProfesional }) => {
  const [imagenes, setImagenes] = useState([]);
  const [file, setFile] = useState(null);


 



useEffect(() => {

  const token = localStorage.getItem('token');
  const url = `http://localhost:3000/imagen/${idProfesional}`;                                       
  axios.get(url, {
    headers: {
      Authorization: `Bearer ${token}`,
    }
  })
  .then((response) => {
    setImagenes(response.data);
  })
  .catch((error) => {
    console.error('Error al cargar im치genes:', error);
  });

}, [idProfesional]);



useEffect(() => {

    

    const token = localStorage.getItem('token');
    const url = 'http://localhost:3000/profesional/imagen';     
    axios.get(url, {                    
      headers: {
        Authorization: `Bearer ${token}`,
      }

    })

    .then((response) => {
      setImagenes(response.data);
    })

    .catch((error) => {
      console.error('//localhost:3000/profesional/imagen', {
      headers: {
        Authorization: `Bearer ${token}`,
      }
    })

    .then((response) => {
      setImagenes(response.data);
    })

    .catch((error) => {
      console.error('Error al cargar im치gen:', error);
    });
    
  })
  }, []);


  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };



const handleSubmit = (event) => {
  event.preventDefault();
  const token = localStorage.getItem('token');
  const formData = new FormData();
  formData.append('file', file);
  console.log(idProfesional); // Verifica el valor de idProfesional
   axios.post(`//localhost:3000/imagen/upload/${idProfesional}`,                                         
   formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
      Authorization: `Bearer ${token}`,
    }
  })
   .then((response) => {
      setImagenes([...imagenes, response.data]);                      
  })
  .catch((error) => {
    console.error('Error al subir imagen:', error);
  });
};



return (
  <div>
    <h3>Trabajos anteriores</h3>
    <div className="imagenes-container" id="imagenes-container">

{imagenes.map((imagen, index) => (
  <img key={index} src={`http://localhost:3000/assets/imagenesUsuarios/${imagen.url}`} alt="Imagen" />
))}

    </div>
    <form onSubmit={handleSubmit}>
      <input type="file" id="file-input" accept="image/*" onChange={handleFileChange} />
      <button type="submit" id="agregar-imagen"> Agregar imagen</button>
    </form>
  </div>
);


};


export default ImagenesProf;
