import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
const PerfilComercial = () => {
    
  
  const { id: idusuarioProfesional } = useParams();
  const [profesional, setProfesional] = useState({});
  const [cargando, setCargando] = useState(true);
    console.log(idusuarioProfesional);  
/*

useEffect(() => {
  axios.get(`http://localhost:3000/profesional/${idusuarioProfesional}`)
    .then(response => {
      console.log(response.data); // Verifica la respuesta de la API
      setProfesional(response.data);
      setCargando(false);
    })
    .catch(error => {
      console.error(error); // Verifica si hay algún error
      setCargando(false);
    });
}, [idusuarioProfesional]);
*/
useEffect(() => {
  axios.get(`http://localhost:3000/profesional/${idusuarioProfesional}`)
    .then(response => {
      if (response.data.length > 0) {
        setProfesional(response.data[0]);
      } else {
        setProfesional(null);
      }
      setCargando(false);
    })
    .catch(error => {
      console.error(error);
      setCargando(false);
    });
}, [idusuarioProfesional]);


/*

  useEffect(() => {
    axios.get(`http://localhost:3000/profesional/${idusuarioProfesional}`)
      .then(response => {
        setProfesional(response.data);
        setCargando(false);
      })
      .catch(error => console.error(error));
  }, [idusuarioProfesional]);
    console.log(idusuarioProfesional);  
*/


  if (cargando) {
    return <div>Cargando...</div>;
  }



  return (
  <div>
    <h2>Perfil de {profesional?.nombre}</h2>
    <p>Email: {profesional?.email}</p>
    <p>Teléfono: {profesional?.telefono}</p>
    <p>Dirección: {profesional?.direccion}</p>
    {!profesional && <p>No se encontró el perfil</p>}
  </div>
);
 
};
