import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './estilos/trabajosContratados.css';
const TrabajosContratados = ({ idProfesional, idusuarioComun }) => {
  const [trabajos, setTrabajos] = useState([]);
  const [error, setError] = useState(null);
  const fetchTrabajos = async () => {
    try {
      let response;
      if (idProfesional) {
        response = await axios.get(`http://localhost:3000/trabajoContratado/profesional/${idProfesional}`);                                                                  
  
      } else if (idusuarioComun) {
        response = await axios.get(`http://localhost:3000/trabajoContratado/usuario/${idusuarioComun}`);
      }
      console.log(response);
      if (Array.isArray(response.data)) {
        setTrabajos(response.data);
      } else {
        setError('La respuesta de la API no es un array');
      }
    } catch (error) {
      setError(error.message);
    }
  };
  useEffect(() => {
    fetchTrabajos();
  }, [idProfesional, idusuarioComun]);
  return (
    <div className="trabajos-realizados">
      {Array.isArray(trabajos) && trabajos.map((trabajo) => (
        <div key={trabajo.idcontratacion} className="tarjeta-trabajo">
          <h2>{trabajo.rubro}</h2>
          <p>Estado: {trabajo.estado}</p>
          <p>Fecha de contratación: {trabajo.fechaContratacion}</p>
          <p>Valoración: {trabajo.valoracion}</p>
          <p>Comentario: {trabajo.comentario}</p>
          {idProfesional ? (
            <p>Cliente: {trabajo.usuarioComun?.nombre}</p>
          ) : (
            <p>Profesional: {trabajo.profesional?.nombre}</p>
          )}
          <div>
            {trabajo.estado === 'pendiente' && (
              <>
                <button>Finalizar</button>
                <button>Cancelar</button>
              </>
            )}
          </div>
        </div>
      ))}
      {error && <p>Error: {error}</p>}
{usuario.tipo === 'comun' && (
  <section className="trabajos-contratados">
    <h3>Trabajos contratados</h3>
    <div id="trabajos-container">
      <TrabajosContratados idusuarioComun={usuario.idusuarioComun} />
    </div>
  </section>
)}



    </div>
  );
};
export default TrabajosContratados;
