import React, { useState, useEffect } from 'react';
import axios from 'axios';
const CarruselOficios = () => {
  const [oficios, setOficios] = useState([]);
  useEffect(() => {
 axios.get('http://localhost:3000/oficios')                            
      .then(response => setOficios(response.data))
      .catch(error => console.error(error));
  }, []);
  const handleClick = (categoria) => {
    localStorage.setItem('categoria', categoria);
    window.location.href = 'pages/listaProfesionales.html';
  };
  return (
    <div className="oficio-container" id="oficio-container">
      {oficios.map((oficio, index) => (
        <article key={index} className="oficio-item">
          <a className="Acard" data-categoria={oficio.nombre} href=" ">                                
            <button className="btnIndexcard">
              <img src={oficio.urlImagen} alt={oficio.nombre} />
            </button>
            <h2>{oficio.nombre}</h2>
          </a>
        </article>
        
      ))}
      <div className="cajaBtn1">
                <button id="anterior1">&#60;</button>
                <h2 className="catalogoCartel">Oficios</h2>
                <button id="siguiente1">&#62;</button>
              </div>
    </div>

  );
};
export default CarruselOficios;
