import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './estilos/Buscador.css'; // Importa el archivo CSS

const Buscador = () => {
  const [textoBusqueda, setTextoBusqueda] = useState('');
  const [resultados, setResultados] = useState([]);
  const [error, setError] = useState(null);
  const handleInputChange = async (e) => {
    setTextoBusqueda(e.target.value);
    setError(null);
    if (e.target.value.length > 0) {
      try {
        const response = await axios.get(`http://localhost:3000/oficios?nombre_like=${e.target.value}`);
        if (Array.isArray(response.data)) {
          setResultados(response.data);
        } else {
          setError('La respuesta del servidor no es válida');
        }
      } catch (error) {
        setError('Error al realizar la búsqueda');
        console.error(error);
      }
    } else {
      setResultados([]);
    }
  };
  return (

  <div className="buscador-container">
    <div className="buscador-container1">
    <input type="text" value={textoBusqueda} onChange={handleInputChange} placeholder="Buscar..." />
    {error && <p style={{ color: 'red' }}>{error}</p>}
    {resultados.length > 0 && (
     <ul className="resultados">
        {resultados.map((resultado, index) => (
          <li key={index}>{resultado.nombre}</li>
        ))}
      </ul>
    )}
    </div>
    <div>
    <a id="irTodos" href="pages/listaOficios.html">
        <button>Todos los Oficios</button>
    </a>
    </div>
  </div>





      );
};
export default Buscador;
