import React, { useState, useEffect } from 'react';
const CarruselPropaganda = () => {
  const [publicidad, setPublicidad] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);
  useEffect(() => {
    fetch('datos/publicidad.json')
      .then(response => response.json())
      .then(data => setPublicidad(data.publicidad));
  }, []);
  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentSlide((currentSlide + 1) % publicidad.length);
    }, 3000);
    return () => clearInterval(intervalId);
  }, [currentSlide, publicidad]);
  const handleClick = (url) => {
    window.open(url, '_blank');
  };
  return (
    <div className="propaganda-container" id="propaganda1">
      {publicidad.map((anuncio, index) => (
        <article
          key={index}
          className="propaganda2"
          data-url={anuncio.pagina}
          onClick={() => handleClick(anuncio.pagina)}
          style={{
            transform: `translateX(${(index - currentSlide) * 100}%)`,
            transition: 'transform 0.5s',
          }}
        >
          <h2>{anuncio.nombre}</h2>
          <img src={anuncio.imagen} alt={anuncio.nombre} />
        </article>
      ))}
    </div>
  );
};
export default CarruselPropaganda;
