// DatosPersonales.js
import React, { useState, useEffect } from 'react';
const DatosPersonales = ({ usuario, editando, handleGuardarCambios, handleCancelar }) => {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [email, setEmail] = useState('');
  const [telefono, setTelefono] = useState('');
  const [direccion, setDireccion] = useState('');
  const [oficio, setOficio] = useState('');
  const [empresa, setEmpresa] = useState('');
  const [descripcion, setDescripcion] = useState(usuario.descripcion);



useEffect(() => {
  setDescripcion(usuario.descripcion);
}, 

[usuario.descripcion]);
const handleDescripcionChange = (e) => {
  setDescripcion(e.target.value);
};


  
  useEffect(() => {
    if (usuario) {
       console.log(usuario); // Agrega este console.log para ver qué datos están llegando
      setNombre(usuario.nombre);
      setApellido(usuario.apellido);
      setEmail(usuario.email);
      setTelefono(usuario.telefono);
      setDireccion(usuario.direccion);
      setOficio(usuario.oficio ? usuario.oficio.nombre : '');
      setEmpresa(usuario.empresa || '');
      setDescripcion(usuario.descripcion);
     
    }
  }, [usuario]);
  const handleNombreChange = (e) => {
    setNombre(e.target.value);
  };
  const handleApellidoChange = (e) => {
    setApellido(e.target.value);
  };
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };
  const handleTelefonoChange = (e) => {
    setTelefono(e.target.value);
  };
  const handleDireccionChange = (e) => {
    setDireccion(e.target.value);
  };
  const handleOficioChange = (e) => {
    setOficio(e.target.value);
  };
  const handleEmpresaChange = (e) => {
    setEmpresa(e.target.value);
  };
  const handleGuardar = () => {
    const datosActualizados = {
      nombre,
      apellido,
      email,
      telefono,
      direccion,
      oficio,
      empresa,
      descripcion,
    };
    handleGuardarCambios(datosActualizados);
  };
  return (
    <div>
      <p>
        <strong>Nombre y apellido:</strong>
        {editando ? (
          <input type="text" value={nombre} onChange={handleNombreChange} />
        ) : (
          <span>{nombre} {apellido}</span>
        )}
      </p>
      <p>
        <strong>Email:</strong>
        {editando ? (
          <input type="email" value={email} onChange={handleEmailChange} />
        ) : (
          <span>{email}</span>
        )}
      </p>
      <p>
        <strong>Teléfono:</strong>
        {editando ? (
          <input type="tel" value={telefono} onChange={handleTelefonoChange} />
        ) : (
          <span>{telefono}</span>
        )}
      </p>
      <p>
        <strong>Dirección:</strong>
        {editando ? (
          <input type="text" value={direccion} onChange={handleDireccionChange} />
        ) : (
          <span>{direccion}</span>
        )}
      </p>
      {usuario.tipo === 'profesional' && (
        <>
          <p>
            <strong>Oficio:</strong>
            {editando ? (
              <input type="text" value={oficio.nombre} onChange={handleOficioChange} />
            ) : (
              <span>{oficio.nombre}</span>
            )}
          </p>

          <p>
  <strong>Empresa:</strong>
  {editando ? (
    <input type="text" value={empresa} onChange={(e) => setEmpresa(e.target.value)} />
  ) : (
    <span>{empresa}</span>
  )}
</p>

<p className="descripcion" id="descripcion-section">
        <div className="descripcion-header">
         <strong>Descripción de habilidades, conocimientos y trabajos anteriores</strong>
        </div>
        {editando ? (
         
<input type="text" value={descripcion} onChange={handleDescripcionChange} />
 
  ) : (
    <span>{usuario.descripcion}</span>
  )}
         
     </p>

        </>
      )} 
    </div>
  );
};
export default DatosPersonales;
