import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './estilos/PerUsuarioComun.css';
import DatosPersonales from './DatosUsuario';

const PerfilUsuario = () => {
  const [usuario, setUsuario] = useState({});
  const [editando, setEditando] = useState(false);
  const [oficio, setOficio] = useState('');
  const [empresa, setEmpresa] = useState('');
  const [descripcion, setDescripcion] = useState('');


  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get("http://localhost:3000/profesional/perfil", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((response) => {
          console.log('Datos del usuario:', response.data);
          setUsuario(response.data);
          if (response.data.tipo === 'profesional') {
            axios.get("http://localhost:3000/profesional/perfil", {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            })
              .then((responseProfesional) => {
                console.log('Datos del profesional:', responseProfesional.data);
                setOficio(responseProfesional.data.oficio?.nombre || '');
                setEmpresa(responseProfesional.data.empresa?.nombre || '');
                setDescripcion(responseProfesional.data.descripcion || '');
              })
              .catch((error) => {
                console.error('Error al cargar datos del profesional:', error);
              });
          }
        })
        .catch((error) => {
          console.error('Error al cargar datos del usuario:', error);
        });
    } else {
      console.log('No hay token');
    }
  }, []);



  const handleEditarDatos = () => {
    setEditando(true);
  };
  const handleGuardarCambios = () => {
    setEditando(false);

  };
  const handleCancelar = () => {
    setEditando(false);
  };
  return (
    <main className="cuenta-container">
      <section className="perfil-usuario">
        <div className="avatar-container">
          <img src={usuario.avatar} alt="Avatar" className="avatar-img" />
          <div className="cambiar-avatar">+</div>
          <input type="file" accept="image/*" hidden />
        </div>
        <div className="info">
          <h2>Mi perfil</h2>
          <DatosPersonales
            usuario={usuario}
            editando={editando}
            handleGuardarCambios={handleGuardarCambios}
            handleCancelar={handleCancelar}
          />

          {usuario.tipo === 'profesional' && (
            <div>


            </div>
          )}
          <p>
            <strong>Estado de cuenta:</strong>
            <span>{usuario.estadoCuenta ? 'Activo' : 'Inactivo'}</span>
          </p>
        </div>
        <div className="botones-edicion">
          {editando ? (
            <>
              <button onClick={handleGuardarCambios}>✔️ Guardar cambios</button>
              <button onClick={handleCancelar}>❌ Cancelar</button>
            </>
          ) : (
            <button onClick={handleEditarDatos}>✏️ Modificar datos</button>
          )}
        </div>
      </section>
      <section className="descripcion" id="descripcion-section">
        <div className="descripcion-header">
          <h3>Descripción de habilidades, conocimientos y trabajos anteriores</h3>
        </div>
        <div id="descripcion-texto" contentEditable={false} className="descripcion-texto">
          {usuario.descripcion}
        </div>
      </section>
      <section className="galeria" id="galeria-section">
        <h3>Trabajos anteriores</h3>
        <div className="imagenes-container" id="imagenes-container">
          {/* Aquí puedes mapear las imágenes del usuario */}
        </div>
        <input type="file" id="file-input" accept="image/*" hidden />
        <button id="agregar-imagen">➕ Agregar imagen</button>
      </section>
      <section className="trabajos-contratados">
        <h3>Trabajos contratados</h3>
        <div id="trabajos-container">
          {/* Aquí puedes mapear los trabajos contratados del usuario */}
        </div>
      </section>
    </main>
  );
};

export default PerfilUsuario;
