import React, { useState, useEffect } from 'react';
const Buscador = () => {
  const [textoBusqueda, setTextoBusqueda] = useState('');
  const [resultados, setResultados] = useState([]);
  const [oficiosData, setOficiosData] = useState([]);
  useEffect(() => {
    fetch('datos/datos.json')
      .then(response => response.json())
      .then(data => setOficiosData(data.oficios));
  }, []);
  const handleInputChange = (e) => {
    setTextoBusqueda(e.target.value);
    const resultadosFiltrados = oficiosData.filter(oficio => oficio.nombre.toLowerCase().includes(e.target.value.toLowerCase()));
    setResultados(resultadosFiltrados);
  };
  const handleResultadoClick = (resultado) => {
    localStorage.setItem('categoria', resultado.nombre);
    window.location.href = 'pages/listaProfesionales.html';
  };
  return (
    <div>
      <input type="text" value={textoBusqueda} onChange={handleInputChange} placeholder="Buscar..." />
      {resultados.length > 0 && (
        <ul>
          {resultados.map((resultado, index) => (
            <li key={index} onClick={() => handleResultadoClick(resultado)}>
              {resultado.nombre}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};
export default Buscador;
